const container=document.getElementsByClassName("container"),search=document.getElementById("search"),data=[];async function fetchData(url){const response=await fetch(url),result=await response.json();UpdatingDom(result),data.push(result)}search.addEventListener("keyup",e=>{let inputFilter=e.target.value.toUpperCase(),cardContainer,cards;document.getElementsByClassName("container")[0].querySelectorAll(".card").forEach(card=>{let cardInfo,name,compare=card.getElementsByClassName("back")[0].getElementsByTagName("h2").name.innerText.startsWith(inputFilter);card.style.display=compare?"block":"none"})});const characters=fetchData("https://www.breakingbadapi.com/api/characters");function UpdatingDom(characters){characters.map(character=>{const{img:img,name:name,birthday:birthday,nickname:nickname,status:status,occupation:occupation}=character,card=document.createElement("div");card.classList.add("card"),card.innerHTML=`<div class="front"><img src="${img}"/></div>\n        <div class="back">\n        <h2 id="name"> ${name}</h2>\n        <small>(${nickname})</small>     \n        <h3>DOB: ${birthday}</h3>\n       <h3 class=${"Alive"==status?"sta-g":"sta-r"}>STATUS: ${status}</h3>  \n       <hr>\n       <p> ${occupation}</p> \n        </div>`,container[0].appendChild(card)})}